import{_ as t,c as e,o as l,aS as r}from"./chunks/framework.Cuoppmxr.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{"aside":false},"headers":[],"relativePath":"skill/management/pmp/PMP/ITTO图/11. 项目风险管理.md","filePath":"skill/management/pmp/PMP/ITTO图/11. 项目风险管理.md","lastUpdated":1734252055000}'),n={name:"skill/management/pmp/PMP/ITTO图/11. 项目风险管理.md"},s=r('<table><thead><tr><th style="text-align:center;"><strong>过程</strong></th><th style="text-align:left;"><strong>定义</strong></th><th style="text-align:left;"><strong>输入</strong></th><th style="text-align:left;"><strong>工具与技术</strong></th><th style="text-align:left;"><strong>输出</strong></th></tr></thead><tbody><tr><td style="text-align:center;"><strong>11.1 规划风险管理</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目章程<br>2.项目管理计划<br>3.项目文件（相关方登记册）<br>4.事业环境因素<br>5.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据分析（相关方分析）<br>3.会议</td><td style="text-align:left;">1.风险管理计划</td></tr><tr><td style="text-align:center;"><strong>11.2 识别风险</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（需求、进度、成本、质量、资源、风险管理计划、范围、进度、成本基准）<br>2.项目文件（假设日志、成本估算、持续时间估算、问题日志、经验教训登记册、需求文件、资源需求、相关方登记册）<br>3.协议<br>4.事业环境因素<br>5.组织过程资产<br>6.采购文档</td><td style="text-align:left;">1.专家判断<br>2.数据收集（头脑风暴、核对单、访谈）<br>3.数据分析（根本原因分析、假设条件和制约因素分析、SWOT分析、文件分析）<br>4.人际关系与团队技能（引导）<br>5.提示清单<br>6.会议</td><td style="text-align:left;">1.风险登记册<br>2.风险报告<br>3.项目文件更新（假设日志、问题日志、经验教训登记册）</td></tr><tr><td style="text-align:center;"><strong>11.3 实施定性风险分析</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（风险管理计划）<br>2.项目文件（假设日志、风险登记册、相关方登记册）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（访谈）<br>3.数据分析（风险数据质量评估、风险概率和影响评估、其他风险参数评估）<br>4.人际关系与团队技能（引导）<br>5.风险分类<br>6.数据表现（概率和影响矩阵、层级型）<br>7.会议</td><td style="text-align:left;">1.项目文件更新（假设日志、问题日志、风险登记册、风险报告）</td></tr><tr><td style="text-align:center;"><strong>11.4 实施定量风险分析</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（风险管理计划、范围、进度、成本基准）<br>2.项目文件（假设日志、估算依据、成本估算、成本预测、持续时间估算、里程碑清单、资源需求、风险登记册、风险报告、进度预测）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（访谈）<br>3.人际关系与团队技能（引导）<br>4.不确定性表现方式<br>5.数据分析（模拟、敏感性分析、决策树分析、影响图）</td><td style="text-align:left;">1.项目文件更新（风险报告）</td></tr><tr><td style="text-align:center;"><strong>11.5 规划风险应对</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源、风险管理计划、成本基准）<br>2.项目文件（经验教训登记册、项目进度计划、项目团队派工单、资源日历、风险登记册、风险报告、相关方登记册）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（访谈）<br>3.人际关系与团队技能（引导）<br>4.威胁应对策略<br>5.机会应对策略<br>6.应急应对策略<br>7.整体项目风险应对策略<br>8.数据分析（备选方案分析、成本效益分析）<br>9.决策（多标准决策分析）</td><td style="text-align:left;">1.变更请求<br>2.项目管理计划更新（进度、成本、质量、资源、采购管理计划、范围、进度、成本基准）<br>3.项目文件更新（假设日志、成本预测、经验教训登记册、项目进度计划、项目团队派工单、风险登记册、风险报告）</td></tr><tr><td style="text-align:center;"><strong>11.6 实施风险应对</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（风险管理计划）<br>2.项目文件（经验教训登记册、风险登记册、风险报告）<br>3.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.人际关系与团队技能（影响力）<br>3.项目管理信息系统</td><td style="text-align:left;">1.变更请求<br>2.项目文件更新（问题日志、经验教训登记册、项目团队派工单、风险登记册、风险报告）</td></tr><tr><td style="text-align:center;"><strong>11.7 监督风险</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划<br>2.项目文件（问题日志、经验教训登记册、风险登记册、风险报告）<br>3.工作绩效数据<br>4.工作绩效报告</td><td style="text-align:left;">1.数据分析（技术绩效分析、储备分析）<br>2.审计<br>3.会议</td><td style="text-align:left;">1.工作绩效信息<br>2.变更请求<br>3.项目管理计划更新<br>4.项目文件更新（假设日志、问题日志、经验教训登记册、风险登记册、风险报告）<br>5.组织过程资产更新</td></tr></tbody></table>',1),d=[s];function a(g,b,i,o,x,y){return l(),e("div",null,d)}const c=t(n,[["render",a]]);export{_ as __pageData,c as default};
