import{_ as t,c as e,o as r,aS as l}from"./chunks/framework.BItddkxz.js";const y=JSON.parse('{"title":"","description":"","frontmatter":{"aside":false},"headers":[],"relativePath":"skill/management/pmp/PMP/ITTO图/13. 项目相关方管理.md","filePath":"skill/management/pmp/PMP/ITTO图/13. 项目相关方管理.md","lastUpdated":1736934269000}'),n={name:"skill/management/pmp/PMP/ITTO图/13. 项目相关方管理.md"},s=l('<table><thead><tr><th style="text-align:center;"><strong>过程</strong></th><th style="text-align:left;"><strong>定义</strong></th><th style="text-align:left;"><strong>输入</strong></th><th style="text-align:left;"><strong>工具与技术</strong></th><th style="text-align:left;"><strong>输出</strong></th></tr></thead><tbody><tr><td style="text-align:center;"><strong>13.1 识别相关方</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目章程<br>2.商业文件（商业论证、效益管理计划）<br>3.项目管理计划（沟通管理计划、相关方参与计划）<br>4.项目文件（变更日志、问题日志、需求文件）<br>5.协议<br>6.事业环境因素<br>7.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（问卷调查、头脑风暴）<br>3.数据分析（相关方分析、文件分析）<br>4.数据表现（权力利益方格、相关方立方体、凸显模型、影响方向、优先级排序）<br>5.会议</td><td style="text-align:left;">1.相关方登记册<br>2.变更请求<br>3.项目管理计划更新（需求、沟通、风险管理计划、相关方参与计划）<br>4.项目文件更新（假设日志、问题日志、风险登记册）</td></tr><tr><td style="text-align:center;"><strong>13.2 规划相关方参与</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目章程<br>2.项目管理计划（资源、沟通、风险管理计划）<br>3.项目文件（假设日志、变更日志、问题日志、项目进度计划、风险登记册、相关方登记册）<br>4.协议<br>5.事业环境因素<br>6.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（标杆对照）<br>3.数据分析（假设条件和制约因素分析）<br>4.决策（优先级排序/分级）<br>5.数据表现（思维导图、相关方参与度评估矩阵）<br>6.会议</td><td style="text-align:left;">1.相关方参与计划</td></tr><tr><td style="text-align:center;"><strong>13.3 管理相关方参与</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（沟通、风险、变更管理计划、相关方参与计划）<br>2.项目文件（变更日志、问题日志、经验教训登记册、相关方登记册）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.沟通技能（反溃）<br>3.人际关系与团队技能（冲突管理、文化意识、谈判、观察/交谈、政治意识）<br>4.基本规则<br>5.会议</td><td style="text-align:left;">1.变更请求<br>2.项目管理计划更新（沟通管理计划、相关方参与计划）<br>3.项目文件更新（变更日志、问题日志、经验教训登记册、相关方登记册）</td></tr><tr><td style="text-align:center;"><strong>13.4 监督相关方参与</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源、沟通管理计划、相关方参与计划）<br>2.项目文件（问题日志、经验教训登记册、风险登记册、相关方登记册）<br>3.工作绩效数据<br>4.事业环境因素<br>5.组织过程资产</td><td style="text-align:left;">1.数据分析（备选方案分析、根本原因分析、相关方分析）<br>2.决策（多标准决策分析、投票）<br>3.数据表现（相关方参与度评估矩阵）<br>4.沟通技能（反馈、演示）<br>5.人际关系与团队技能（积极倾听、文化意识、领导力、人际交往、政治意识）<br>6.会议</td><td style="text-align:left;">1.工作绩效信息<br>2.变更请求<br>3.项目管理计划更新（资源、沟通管理计划、相关方参与计划）<br>4.项目文件更新（问题日志、经验教训登记册、风险登记册、相关方登记册）</td></tr></tbody></table>',1),a=[s];function d(b,g,i,o,_,f){return r(),e("div",null,a)}const c=t(n,[["render",d]]);export{y as __pageData,c as default};
