import{_ as t,c as e,o as r,aS as l}from"./chunks/framework.Cuoppmxr.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{"aside":false},"headers":[],"relativePath":"skill/management/pmp/PMP/ITTO图/4. 项目整合管理.md","filePath":"skill/management/pmp/PMP/ITTO图/4. 项目整合管理.md","lastUpdated":1720893388000}'),n={name:"skill/management/pmp/PMP/ITTO图/4. 项目整合管理.md"},s=l('<table><thead><tr><th style="text-align:center;"><strong>过程</strong></th><th style="text-align:left;"><strong>定义</strong></th><th style="text-align:left;"><strong>输入</strong></th><th style="text-align:left;"><strong>工具与技术</strong></th><th style="text-align:left;"><strong>输出</strong></th></tr></thead><tbody><tr><td style="text-align:center;"><strong>4.1 制定项目章程</strong></td><td style="text-align:left;">编写一份正式批准项目并授权项目经理在项目活动中使用组织资源的文件的过程。</td><td style="text-align:left;">1.商业文件（商业论证、效益管理计划）<br>2.协议<br>3.事业环境因素4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（头脑风暴、焦点小组、访谈）<br>3.人际关系与团队技能（冲突管理、引导、会议管理）<br>4.会议</td><td style="text-align:left;">1.项目章程<br>2.假设日志</td></tr><tr><td style="text-align:center;"><strong>4.2 制定项目管理计划</strong></td><td style="text-align:left;">定义、准备和协调项目计划的所有组成部分，并把它们整合为一份综合项目管理计划的过程。</td><td style="text-align:left;">1.项目章程<br>2.其他过程的输出<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据收集（头脑风暴、核对单、焦点小组、访谈）<br>3.人际关系与团队技能（冲突管理、引导、会议管理）<br>4.会议</td><td style="text-align:left;">1.项目管理计划</td></tr><tr><td style="text-align:center;"><strong>4.3 指导与管理项目工作</strong></td><td style="text-align:left;">为实现项目目标而领导和执行项目管理计划中所确定的工作，并实施已批准变更的过程。</td><td style="text-align:left;">1.项目管理计划<br>2.其他过程的输出<br>3.批准的变更请求<br>4.事业环境因素<br>5.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.项目管理信息系统<br>3.会议</td><td style="text-align:left;">1.可交付成果<br>2.工作绩效数据<br>3.问题日志<br>4.变更请求<br>5.项目管理计划更新<br>6.项目文件更新（活动清单、假设日志、经验教训登记册、需求文件、风险登记册、相关方登记册）<br>7.组织过程资产更新</td></tr><tr><td style="text-align:center;"><strong>4.4 管理项目知识</strong></td><td style="text-align:left;">使用现有知识并生成新知识，以实现项目目标，并且帮助组织学习的过程。</td><td style="text-align:left;">1.项目管理计划<br>2.项目文件（经验教训登记册、项目团队派工单、资源分解结构、供方选择标准、相关方登记册）<br>3.可交付成果<br>4.事业环境因素<br>5.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.知识管理<br>3.信息管理<br>4.人际关系与团队技能（积极倾听、引导、领导力、人际交往、政治意识）</td><td style="text-align:left;">1.经验教训登记册<br>2.项目管理计划更新<br>3.组织过程资产更新</td></tr><tr><td style="text-align:center;"><strong>4.5 监控项目工作</strong></td><td style="text-align:left;">跟踪、审查和报告整体项目进展，以实现项目管理计划中确定的绩效目标的过程。</td><td style="text-align:left;">1.项目管理计划<br>2.项目文件<br>3.工作绩效信息<br>4.协议<br>5.事业环境因素<br>6.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据分析（备选方案分析、成本效益分析、挣值分析、根本原因分析、趋势分析、偏差分析）<br>3.决策<br>4.会议</td><td style="text-align:left;">1.工作绩效报告<br>2.变更请求<br>3.项目管理计划更新<br>4.项目文件更新（成本预测、问题日志、经验教训登记册、风险登记册、进度预测）</td></tr><tr><td style="text-align:center;"><strong>4.6 实施整体变更控制</strong></td><td style="text-align:left;">审查所有变更请求，批准变更，管理对可交付成果、组织过程资产、项目文件和项目管理计划的变更，并对变更处理结果进行沟通的过程。</td><td style="text-align:left;">1.项目管理计划（变更管理计划、配置管理计划、范围基准、进度基准、成本基准）<br>2.项目文件（估算依据、需求跟踪矩阵、风险报告）<br>3.工作绩效报告<br>4.变更请求<br>5.事业环境因素<br>6.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.变更控制工具<br>3.数据分析（备选方案分析、成本效益分析）<br>4.决策（投票、独裁、多标准决策分析）<br>5.会议</td><td style="text-align:left;">1.批准的变更请求<br>2.项目管理计划更新<br>3.项目文件更新（变更日志）</td></tr><tr><td style="text-align:center;"><strong>4.7 结束项目或阶段</strong></td><td style="text-align:left;">终结项目、阶段或合同的所有活动的过程。</td><td style="text-align:left;">1.项目章程<br>2.项目管理计划<br>3.项目文件<br>4.验收的可交付成果<br>5.商业文件<br>6.协议<br>7.采购文档<br>8.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据分析（文件、回归、趋势、偏差分析）<br>3.会议</td><td style="text-align:left;">1.项目文件更新（经验教训登记册）<br>2.最终产品、服务或成果移交<br>3.最终报告<br>4.组织过程资产更新</td></tr></tbody></table>',1),d=[s];function a(b,g,i,o,x,y){return r(),e("div",null,d)}const c=t(n,[["render",a]]);export{_ as __pageData,c as default};
