import{_ as t,c as e,o as r,aS as l}from"./chunks/framework.Cuoppmxr.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{"aside":false},"headers":[],"relativePath":"skill/management/pmp/PMP/ITTO图/9. 项目资源管理.md","filePath":"skill/management/pmp/PMP/ITTO图/9. 项目资源管理.md","lastUpdated":1720882388000}'),n={name:"skill/management/pmp/PMP/ITTO图/9. 项目资源管理.md"},s=l('<table><thead><tr><th style="text-align:center;"><strong>过程</strong></th><th style="text-align:left;"><strong>定义</strong></th><th style="text-align:left;"><strong>输入</strong></th><th style="text-align:left;"><strong>工具与技术</strong></th><th style="text-align:left;"><strong>输出</strong></th></tr></thead><tbody><tr><td style="text-align:center;"><strong>9.1 规划资源管理</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目章程<br>2.项目管理计划（质量管理计划、范围基准）<br>3.项目文件（项目进度计划、需求文件、风险登记册、相关方登记册）<br>4.事业环境因素<br>5.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.数据表现（层级型、责任分配矩阵、文本型）<br>3.组织理论<br>4.会议</td><td style="text-align:left;">1.资源管理计划<br>2.团队章程<br>3.项目文件更新（假设日志、风险登记册）</td></tr><tr><td style="text-align:center;"><strong>9.2 估算活动资源</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源管理计划、范围基准）<br>2.项目文件（活动属性、活动清单、假设日志、成本估算、资源日历、风险登记册）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.专家判断<br>2.自下而上估算<br>3.类比估算<br>4.参数估算<br>5.数据分析（备选方案分析）<br>6.项目管理信息系统<br>7.会议</td><td style="text-align:left;">1.资源需求<br>2.估算依据<br>3.资源分解结构<br>4.项目文件更新（活动属性、假设日志、经验教训登记册）</td></tr><tr><td style="text-align:center;"><strong>9.3 获取资源</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源管理计划、采购管理计划、成本基准）<br>2.项目文件（项目进度计划、资源日历、资源需求、相关方登记册）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.决策（多标准决策分析）<br>2.人际关系与团队技能（谈判）<br>3.预分派<br>4.虚拟团队</td><td style="text-align:left;">1.物质资源分配单<br>2.项目团队派工单<br>3.资源日历<br>4.变更请求<br>5.项目管理计划更新（资源管理计划、成本基准）<br>6.项目文件更新（经验教训登记册、项目进度计划、资源分解结构、资源需求、风险登记册、相关方登记册）<br>7.事业环境因素更新<br>8.组织过程资产更新</td></tr><tr><td style="text-align:center;"><strong>9.4 建设团队</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源管理计划）<br>2.项目文件（经验教训登记册、项目进度计划、项目团队派工单、资源日历、团队章程）<br>3.事业环境因素<br>4.组织过程资产</td><td style="text-align:left;">1.集中办公<br>2.虚拟团队<br>3.沟通技术<br>4.人际关系与团队技能（冲突管理、影响力、激励、谈判、团队建设）<br>5.认可与奖励<br>6.培训<br>7.个人和团队评估<br>8.会议</td><td style="text-align:left;">1.团队绩效评估<br>2.变更请求<br>3.项目管理计划更新（资源管理计划更新）<br>4.项目文件更新（经验教训登记册、项目进度计划、项目团队派工单、资源日历、团队章程）<br>5.事业环境因素更新<br>6.组织过程资产更新</td></tr><tr><td style="text-align:center;"><strong>9.5 管理团队</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源管理计划）<br>2.项目文件（问题日志、经验教训登记册、项目团队派工单、团队单程）<br>3.工作绩效报告<br>4.团队绩效评价<br>5.事业环境因素<br>6.组织过程资产</td><td style="text-align:left;">1.人际关系与团队技能（冲突管理、制定决策、情商、影响力、领导力）<br>2.项目管理信息系统</td><td style="text-align:left;">1.变更请求<br>2.项目管理计划更新（资源管理计划、进度基准、成本基准）<br>3.项目文件更新（问题日志、经验教训登记册、项目团队派工单）<br>4.事业环境因素更新</td></tr><tr><td style="text-align:center;"><strong>9.6 控制资源</strong></td><td style="text-align:left;"></td><td style="text-align:left;">1.项目管理计划（资源管理计划）<br>2.项目文件（问题日志、经验教训登记册、物质资源分配单、项目进度计划、资源分解结构、资源需求、风险登记册）<br>3.工作绩效数据<br>4.协议<br>5.组织过程资产</td><td style="text-align:left;">1.数据分析（备选方案分析、成本效益分析、绩效审查、趋势分析）<br>2.问题解决<br>3.人际关系与团队技能（谈判、影响力）<br>4.项目管理信息系统</td><td style="text-align:left;">1.工作绩效信息<br>2.变更请求<br>3.项目管理计划更新（资源管理计划、进度基准、成本基准）<br>4.项目文件更新（假设日志、问题日志、经验教训登记册、物质资源分配单、资源分解结构、风险登记册）</td></tr></tbody></table>',1),d=[s];function a(b,g,i,o,x,f){return r(),e("div",null,d)}const c=t(n,[["render",a]]);export{_ as __pageData,c as default};
